import React, { Component } from 'react';
import { withRouter } from 'react-router';
import { Container, Row, Col, Button } from 'react-bootstrap';
import Footer from 'components/Layout/Footer/Footer';
import './Login.css';

export const authEndpoint = 'https://accounts.spotify.com/authorize';
const clientId = "8f6b07f84e98420e8ad154cf942adee6";
const redirectUri = "http://localhost:3000";
const scopes = [
  "user-read-private",
  "user-read-email",
  "user-read-playback-state",
  "playlist-modify-public",
  "user-top-read"
];

class Login extends Component {
    constructor(){
        super();
    }

    loginClickHandler = () => {
        console.log('hi');
        fetch('/api/login/');
      }

    render() {

        return(
                <React.Fragment>
<Container className = "about-section" fluid>
        <Row><Col>
        <div className="container login-section">
      <div id="login">
        <h1 className="title">re-set</h1>
        <h2>Log in with Spotify to get started.</h2>
        <Button className="spotify-login" onClick={() => this.loginClickHandler()}>Login with Spotify</Button>
      </div>
    </div>
        </Col></Row>
</Container>
<Footer />
</React.Fragment>
        )
    }   
}

export default withRouter(Login);
