import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';
import 'containers/Search/Search.css';

class Search extends Component {
       constructor(){
                super();
                this.state = {
                loading: false,
                showArrow: false,
                topArtists: null
                }
                } 

        render () {
                let arrow;
                if(this.state.showArrow){
                        arrow = <FontAwesomeIcon className="search-arrow" icon={faArrowRight} size ="lg" />;
                } else{
                        arrow = null;
                }

                // showArrowHandler(){

                // }

        return(

<Container className = "search-section" fluid>
        <Row><Col>
                <form>
                <input className="searchBar" type="input" placeholder="Search for an Artist..."></input>
                {arrow}
               </form>
        </Col></Row>
</Container>
                )       
        }
    }

export default Search;
