import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';
import * as spotifyFunctions from 'spotifyFunctions';
import 'containers/Search/Search.css';

class Search extends Component {
       constructor(){
                super();
                this.state = {
                showArrow: false,
                searchValue: ''
        }} 

        searchValueHandler = ( event ) => {

                if(event.target.value.length > 0){
                this.setState({showArrow: true, searchValue: event.target.value});       
                } else{
                        this.setState({showArrow: false, searchValue: event.target.value})
                        } }

        searchHandler = () => {
                this.props.history.push( '/results' );
        }

        render () {
                let arrow;
                if(this.state.showArrow){
                        arrow = <FontAwesomeIcon className="search-arrow" icon={faArrowRight} size ="lg" onClick={this.searchHandler} />;
                } else{
                        arrow = null;
                }

        return(

<Container className = "search-section" fluid>
        <Row><Col>
                <form>
                <input className="searchBar" type="input" placeholder="Search for an Artist..." onChange={this.searchValueHandler}></input>
                {arrow}
               </form>
        </Col></Row>
</Container>
                )       
        }
    }

export default Search;
