import Spotify from 'spotify-web-api-js';

const spotifyWebApi = new Spotify();

var setlistfmClient = new setlistfm({
	key: "cO7hoI_12EQw5tvknp9zmEia4donV992fgpn", // Insert your personal key here
	format: "json", // "json" or "xml", defaults to "json"
	language: "en", // defaults to "en"
});

export function getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

export function setToken (params) {
    spotifyWebApi.setAccessToken(params.access_token);
}

export async function getDisplayName() {
   const response = await spotifyWebApi.getMe();
   return response;
}
