import React, { Component } from 'react';
import { Container } from 'react-bootstrap';
import Navigation from 'components/Navigation/Navigation';
import spotifyWebApi from 'App.js';

class Layout extends Component {

    state = {
        displayName: ''
    }

      componentDidMount () {

        spotifyWebApi.getMe()
        .then((response) => {
          console.log(response);
          this.setState({
            displayName: response.display_name
          })
        })

      }

    render() {
        return(
            <Container>
                <Navigation loggedIn={this.props.loggedIn} displayName={this.props.displayName} />
                {this.props.children}
            </Container>
        );
    }
}

export default Layout;
