import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { TransitionGroup, CSSTransition } from 'react-transition-group';
import Search from 'containers/Search/Search';
import SearchResults from 'containers/SearchResults/SearchResults';
import Layout from 'components/Layout/Layout';
import Footer from 'components/Layout/Footer/Footer';
import * as spotifyFunctions from 'spotifyFunctions';
import 'App.css';

class App extends Component {

  constructor(){
    super();
    const params = spotifyFunctions.getHashParams();
    this.state={
      loggedIn: params.access_token  ? true : false,
      displayName: ''
    }
    if (params.access_token){
      spotifyFunctions.setToken(params);
    }
  }

  componentWillMount () {
    if(this.state.loggedIn){
      spotifyFunctions.getDisplayName()
.then((response =>  
  this.setState(
  {displayName: response.display_name}
 )))
 .catch(function(error){
   console.log(error);
 });
  }
    }


  render() {
      return (
        <Route render={(location) =>(
          <Router>
      <Layout loggedIn={this.state.loggedIn} displayName={this.state.displayName} >
      <TransitionGroup>
        <CSSTransition
        classNames="fade"
          key={location.key}  
          timeout={300}
        >
          <Switch>
            <Route exact path="/" render={props =>
            (<Search {...props} loggedIn={this.state.loggedIn} />)}/>
             <Route path="/results" component={SearchResults} />
             <Route path="/about" component={Search} />
          </Switch>
          </CSSTransition>
      </TransitionGroup>
        </Layout>
        <Footer />
  </Router>
        )} />

  );
  }
}

export default App;
