import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import SetlistResult from 'components/SetlistResult/SetlistResult';
import { withRouter } from "react-router";
import 'containers/Setlists/Setlists.css';
import Spinner from 'components/UI/Spinner/Spinner';

class Setlists extends Component {
       constructor(){
                super();
                this.state = {
                    artistName: '',
                    loading: false,
                    pageResults: null,
                    curPageNum: 1
        }} 

        componentDidMount() {  
            let artist = this.props.location.state.artistName;
            let setlists = null;
            if(artist){
                this.setState({artistName: artist, loading: true});
                fetch('/api/getSetlists/' + artist + '/1')
                .then((response => {
                    setlists = response.json().then(((response)=> {
                        this.setState({pageResults: response.setlist, loading: false, currPageNum: this.state.currPageNum += 1 })
                    }))
                }))
                .catch((error => {
                    this.setState({loading: false});
                    console.log(error);
                }))
            }
        }

        // componentDidUpdate(){

        // }

        render () {
            let results = null;
            if(this.state.loading){
                results = <Spinner />
            }
        return(

<Container className = "setlists-section" fluid>
        {results}
</Container>
                )       
        }
    }

export default withRouter(Setlists);
