import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { withRouter } from "react-router";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDoubleRight } from '@fortawesome/free-solid-svg-icons'
import 'containers/SearchResults/SearchResults.css';
import Spinner from 'components/UI/Spinner/Spinner';
import * as spotifyFunctions from 'spotifyFunctions';

class SearchResults extends Component {
       constructor(){
                super();
                this.state={
                    searchValue: null,
                    loading: false,
                    result: null
                }   
            }

            componentWillMount() {
                console.log(this.props.location.state.searchValue);
                this.setState({searchValue: this.props.location.state.searchValue});
            }

            componentDidMount(){
                if(this.state.searchValue){
                this.setState({loading: true});
                spotifyFunctions.searchForArtist(this.state.searchValue)
              .then((response => {
                let searchResult = response.artists.items;
                this.setState({loading: false, result: searchResult});
              }))   
              .catch((error => {
                this.setState({loading: false});
                console.log(error);
              }));
                }
            }

        render () {
            let displayResults = '';
            if(this.state.result){
                displayResults =  Object.keys( this.state.result )
                .map( artistKey => {
                    return (
                      <Row className="mb-4">
                        <Col md={{ span: 3, offset: 3 }} className="mb-4 my-auto">
                        <img className="roundedImg" src="https://i.scdn.co/image/5ea794cf832550943d5f8122afcf5f23ee9d85b7"/>
                        </Col>
                        <Col md={3} className="resultDetails my-auto">
                        <h2>{this.state.result[artistKey].name}</h2>
                        <FontAwesomeIcon icon={faAngleDoubleRight} color="white" size="lg"/>
                        </Col>
                        </Row>
                        ); } );
            }
            if( this.state.loading ){
                displayResults = <Spinner />
            }

        return(
            <Container className = "results-section" fluid>
              <h1 className="mb-5"><span className="resultsTitle">Results</span></h1>
              {displayResults}
                </Container>
                )       
        }
    }

export default withRouter(SearchResults);
