import React, {Component} from 'react';
import 'components/UI/ScrollUp/ScrollUp.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronCircleUp } from '@fortawesome/free-solid-svg-icons'

class ScrollUp extends Component {
  constructor(){
    super();
    this.state={
        showBtn: false
    }   
}

compontentDidMount(){
  window.addEventListener('scroll', this.scrollHandler);
} 

componentWillUnmount() {
  window.removeEventListener('scroll', this.scrollHandler);
}

scrollToTop = () => {
  window.scrollTo({top: 0, behavior: "smooth"});
  }

  scrollHandler = () => {
    console.log('scroll!');
    if(window.pageYOffset === 0){
      this.setState({showBtn: false});
  }
  else{
    this.setState({showBtn: true});
  }
  }

  render(){
    let button = null;
    if(this.state.showBtn){
      button = <FontAwesomeIcon
      icon={faChevronCircleUp} id="scrollBtn" color="white" size="lg" onClick={this.scrollToTop} />;
    }
    else{
      button =<FontAwesomeIcon
      icon={faChevronCircleUp} id="scrollBtn" className="hideBtn" color="white" size="lg" onClick={this.scrollToTop} />;
    }
    return(
      <React.Fragment>
       {button} 
      </React.Fragment>
    )
  }
}

export default ScrollUp;
