import React, { Component } from 'react';
import { withRouter } from 'react-router';
import { Container, Row, Col, Button } from 'react-bootstrap';
import Footer from 'components/Layout/Footer/Footer';

const clientId = "8f6b07f84e98420e8ad154cf942adee6";
const redirectUri = "http://localhost:3000";
const scopes = [
  "user-read-private",
  "user-read-email",
  "user-read-playback-state",
  "playlist-modify-public",
  "user-top-read"
];

class Login extends Component {
    constructor(){
        super();
    }
    render() {
        return(
                <React.Fragment>
<Container className = "about-section" fluid>
        <Row><Col>
        <div class="container login-section">
      <div id="login">
        <h1 class="title">re-set</h1>
        <h2>Log in with Spotify to get started.</h2>
        <a href={`${authEndpoint}client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join("%20")}&response_type=token&show_dialog=true`} class="btn spotify-login">Log in with Spotify</a>
      </div>
      <div id="loggedin">

        <button class="btn btn-default" id="obtain-new-token">Obtain new token using the refresh token</button>
      </div>
    </div>
        </Col></Row>
</Container>
<Footer />
</React.Fragment>
        )
    }   
}

export default WithRouter(Login);
