import { withRouter } from 'react-router';
import { Container, Col, Row } from 'react-bootstrap';
import Spinner from 'components/UI/Spinner/Spinner';
import 'components/ViewSetlist/ViewSetlist.css';

class ViewSetlist extends Component {
    constructor(props) {
      super();
      this.state = {
        setObject: null,
        loading: false,
        error: false,
        spotifyResponse: null
}}

componentDidMount(){
  let setlist = null;
  let spotifyTracks = null;
            this.setState({loading: true});
                fetch('/api/getSetlist/' + this.props.match.params.id)
                .then((response => {
                    setlist = response.json().then(((response)=> {
                        this.setState({setObject: response, loading: false})
                        console.log(response);
                        Object.entries(response.sets).forEach(
                          (set)=>{
                            set.song.forEach(
                              (song)=>{
                                  console.log(song.name);
                              }
                            )
                          }
                        )

                    }))
                }))
                .catch((error => {
                    this.setState({loading: false, error: true});
                    console.log(error);
                }))
}

fetchSpotifyTrack(){
  console.log('hi!');
}

    render() {
      let displaySetlist = null;

      if(this.state.loading){
        displaySetlist = <Spinner />
      }
      if(this.state.setObject){
        displaySetlist = <Container className="setContainer">
        <Row className="setTitle">
        <h2><span className="titleStyle">{this.state.setObject.artist.name} at {this.state.setObject.venue.name}, {this.state.setObject.eventDate}</span></h2>
        </Row>
        <Row>
      <Col sm={12} md={6} className="set-overview">
     <img src="https://i.scdn.co/image/5ea794cf832550943d5f8122afcf5f23ee9d85b7"/>
     </Col>
      <Col sm={12} md={6} className ="set-details">
     <ul className="songList">
         <li>1. Started From the Bottom</li>
         <li>2. Both</li>
         <li>3. Mob Ties</li>
     </ul>
  </Col>
  </Row>
    </Container>;
      }


      return (
        <React.Fragment>
          {displaySetlist}
          </React.Fragment>
      );
    }
  }

export default withRouter(ViewSetlist);
